SELECT ORDER_ID, PRODUCT_ID, (SELECT DATE_FORMAT(OUT_DATE,'%Y-%m-%d') FROM FOOD_ORDER WHERE OUT_DATE IS NOT NULL AND ORDER_ID = F1.ORDER_ID) AS OUT_DATE, CASE WHEN DATEDIFF(DATE_FORMAT(OUT_DATE,'%Y-%m-%d'), '2022-05-01') <= 0 THEN '출고완료' WHEN DATEDIFF(DATE_FORMAT(OUT_DATE,'%Y-%m-%d'), '2022-05-01') > 0 THEN '출고대기' WHEN OUT_DATE IS NULL THEN '출고미정' END AS '출고여부' FROM FOOD_ORDER AS F1